import React, { useEffect, useState, useContext } from 'react';
import { UserContext } from '../App';
import { ToDo } from '../ToDo/ToDo';
import { Completed } from '../Completed/Completed';
import ls from 'local-storage';
import '../App.css';
import Plus from '../assets/graphics/Plus.svg';

const Protected = props => {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     todos: ls.get('toDos') || [
  //       { title: 'note one', id: '231r' },
  //       { title: 'note two', id: 'efef' },
  //       { title: 'note three', id: 'sd09s' }
  //     ],
  //     message: '',
  //     completed: ls.get('completeds') || [
  //       { title: '1', id: 'grhwg' },
  //       { title: '2', id: '9joi' }
  //     ],
  //     showForm: false,
  //     showCompleted: false
  //   };
  // }

  const [showForm, setShowForm] = useState();
  const [message, setMessage] = useState();
  const [showCompleted, setShowCompleted] = useState();

  const [todos, setTodos] = useState(() => {
    const localData = ls.get('toDos');
    return localData ? JSON.parse(localData) : ['data wiped clean'];
  });
  const [completed, setCompleted] = useState(() => {
    const localData = ls.get('completeds');
    return localData ? JSON.parse(localData) : ['data wiped clean'];
  });

  const addItem = async e => {
    e.preventDefault();
    let newItem = { title: this.newItem.value };
    const isOnTheList = todos.includes(newItem);
    if (isOnTheList) {
      setMessage({
        message: 'This To-do is already on the list.'
      });
    } else {
      newItem =
        { title: this.newItem.value, id: Math.random() } &&
        newItem !== '' &&
        setTodos({ todos: [...todos, newItem] }) &&
        setMessage({ message: 'Added entry to to-do list' }) &&
        (await ls.set('toDos', JSON.stringify(todos)));
    }
    this.addForm.reset();
    await ls.set('toDos', JSON.stringify(todos));
  };

  const completedItem = async item => {
    const newTodos = todos.filter(todo => {
      return todo !== item;
    });
    if (1 === 1) {
      setCompleted({ completed: [...completed, item] });
      setMessage({ message: 'Added to completed list' });
      setTodos({ ...newTodos });
      await ls.set('completeds', JSON.stringify(completed));
      await ls.set('toDos', JSON.stringify([...newTodos]));
    }
    await ls.set('completeds', JSON.stringify(completed));
    await ls.set('toDos', JSON.stringify([...newTodos]));
    const completedStore = await ls.get('completeds');
    console.log('Here is the completed local state: ');
    console.log(JSON.parse(completedStore));
  };

  const removeItem = async item => {
    const newTodos = completed.filter(todo => {
      return todo !== item;
    });
    if (1 === 1) {
      setCompleted({
        completed: [...newTodos]
      });
      setMessage({
        message: 'Deleted old to-do'
      });
      await ls.set('completeds', JSON.stringify([...newTodos]));
    }
    await ls.set('completeds', JSON.stringify([...newTodos]));
    const completedRemovals = await ls.get('completeds');
    console.log('current completeds after removal: ', completedRemovals);
  };

  const toggleInputHandler = () => {
    setShowForm({ showForm: !showForm });
  };
  const toggleCompletedHandler = () => {
    setShowCompleted({ showCompleted: !showCompleted });
  };

  const [user] = useContext(UserContext);

  useEffect(() => {
    async function fetchProtected() {
      const result = await (
        await fetch('http://localhost:4000/protected', {
          method: 'POST',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
            authorization: `Bearer ${user.accesstoken}`
          }
        })
      ).json();
      console.log('user: ', user);
      console.log('result: ', result);
    }
    fetchProtected();
  }, [user]);

  return (
    <div>
      {user.accesstoken ? (
        <React.Fragment>
          <div>
            <div className="header">
              <h1 className="banner">Your To-Do's</h1>
              <p className="msg">{message}</p>
              <img
                src={Plus}
                alt="plus_clickme_show_input_form"
                onClick={toggleInputHandler}
                className="plus"
              />

              {this.state.showForm === true ? (
                <div className="inputForm">
                  <form
                    ref={input => (this.addForm = input)}
                    onSubmit={e => {
                      addItem(e);
                    }}
                  >
                    <div>
                      <input
                        type="text"
                        placeholder="Type To-do Here"
                        id="newItemInput"
                        ref={input => (this.newItem = input)}
                        className="input"
                      />
                      <br />
                      <br />
                      <button className="button" type="submit">
                        Add
                      </button>
                      <br />
                      <br />
                    </div>
                  </form>
                </div>
              ) : null}
            </div>

            <div className="block">
              <table>
                <tbody>
                  <tr>
                    <td>
                      {todos.map(item => (
                        <ToDo
                          click={() => completedItem(item)}
                          title={item.title}
                          key={item.id}
                        />
                      ))}
                    </td>
                  </tr>
                </tbody>
              </table>
              <button className="completedBtn" onClick={toggleCompletedHandler}>
                Show Completed
              </button>
              {this.state.showCompleted === true ? (
                <div>
                  <table>
                    <tbody>
                      <tr>
                        <td>
                          {completed.map(item => (
                            <Completed
                              click={() => removeItem(item)}
                              title={item.title}
                              key={item.id}
                            />
                          ))}
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              ) : null}
            </div>
          </div>
        </React.Fragment>
      ) : (
        <p>'log in to use the app'</p>
      )}
    </div>
  );
};

export default Protected;
